import React, { useState } from "react";
import { useSelector } from "react-redux";
import emailjs from "@emailjs/browser";

const CheckoutForm = () => {
  const items = useSelector((state) => state.items);
  const [formData, setFormData] = useState({
    nom: "",
    prenom: "",
    adresse: "",
    phone: "",
    email: "",
    message: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const panierDetails = items
      .map((item) => `${item.details.name} (x${item.quantity}) - €${(item.quantity * item.details.price).toFixed(2)}`)
      .join("\n");

    const total = items.reduce(
      (sum, item) => sum + item.quantity * item.details.price,
      0
    ).toFixed(2);

    const templateParams = {
      nom: formData.nom,
      prenom: formData.prenom,
      adresse: formData.adresse,
      phone: formData.phone,
      email: formData.email,
      message: formData.message,
      panier: panierDetails,
      total: total
    };

    emailjs.send(
      "service_8jdkmfo",        // Service ID
      "template_seq7myp",       // Template ID
      templateParams,
      "user_qn74gymqp7NIzbNBd1Qn4" // Public Key
    )
    .then(() => {
      alert("✅ Commande envoyée avec succès !");
      setFormData({ nom: "", prenom: "", adresse: "", phone: "", email: "", message: "" });
    })
    .catch((error) => {
      alert("❌ Erreur lors de l'envoi : " + error.text);
      console.error(error);
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Finaliser votre commande</h2>
      <input type="text" name="nom" placeholder="Nom" onChange={handleChange} required />
      <input type="text" name="prenom" placeholder="Prénom" onChange={handleChange} required />
      <input type="text" name="adresse" placeholder="Adresse" onChange={handleChange} required />
      <input type="text" name="phone" placeholder="Téléphone" onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
      <textarea name="message" placeholder="Message" onChange={handleChange}></textarea>
      <button type="submit">Envoyer ma commande</button>
    </form>
  );
};

export default CheckoutForm;
