import React, { useState } from "react";
import { useSelector } from "react-redux";
import emailjs from "emailjs-com";
import "../../styles/App.css";

const CheckoutForm = () => {
  const items = useSelector((state) => state.items); // ✅ Récupère le panier depuis Redux
  const [formData, setFormData] = useState({
    nom: "",
    prenom: "",
    adresse: "",
    message: ""
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Construire le contenu du panier sous forme de texte
    const panierDetails = items.map((item) => {
      return `${item.details.name} (x${item.quantity}) - €${(item.quantity * item.details.price).toFixed(2)}`;
    }).join("\n");

    const templateParams = {
      nom: formData.nom,
      prenom: formData.prenom,
      adresse: formData.adresse,
      message: formData.message,
      panier: panierDetails,
      total: items.reduce((sum, item) => sum + item.quantity * item.details.price, 0).toFixed(2)
    };

    emailjs
      .send(
        "service_8jdkmfo",   // ← ton Service ID
        "template_seq7myp",  // ← ton Template ID
        templateParams,
        "user_qn74gymqp7NlzbNBd1Qn4"     // ← ta clé publique
      )
      .then(
        () => {
          alert("✅ Votre commande a été envoyée à l’administrateur !");
          setFormData({ nom: "", prenom: "", adresse: "", message: "" });
        },
        (error) => {
          alert("❌ Erreur lors de l’envoi, réessayez.");
          console.error(error.text);
        }
      );
  };

  return (
    <div className="container checkout-container">
      <h2>Finaliser votre commande</h2>
      <form onSubmit={handleSubmit} className="checkout-form">
        <div className="form-group">
          <label>Nom :</label>
          <input
            type="text"
            name="nom"
            value={formData.nom}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Prénom :</label>
          <input
            type="text"
            name="prenom"
            value={formData.prenom}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Adresse :</label>
          <input
            type="text"
            name="adresse"
            value={formData.adresse}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Message :</label>
          <textarea
            name="message"
            value={formData.message}
            onChange={handleChange}
            placeholder="Votre message pour l’administrateur..."
          />
        </div>

        <button type="submit" className="btn btn-primary">
          Envoyer ma commande
        </button>
      </form>
    </div>
  );
};

export default CheckoutForm;
